#!/bin/bash
find build/release/bin -maxdepth 1 -type f -name '*' \! -name 'pastad' \! -name 'pasta-blockchain-export' \! -name 'pasta-wallet-cli' \! -name 'pasta-wallet-rpc' \! -name 'pasta-blockchain-import' \! -name 'pasta-blockchain-usage' \! -name 'pasta-address-validator' \! -name 'pasta-gen-trusted-multisig' \! -name 'pasta-blockchain-blackball' \! -name 'pasta-utils-deserialize' \! -name 'pasta-utils-object-sizes' \! -name '*.log' -exec mv -vn {} test-wallets \;
find build/debug/bin -maxdepth 1 -type f -name '*' \! -name 'pastad' \! -name 'pasta-blockchain-export' \! -name 'pasta-wallet-cli' \! -name 'pasta-wallet-rpc' \! -name 'pasta-blockchain-import' \! -name 'pasta-blockchain-usage' \! -name 'pasta-address-validator' \! -name 'pasta-gen-trusted-multisig' \! -name 'pasta-blockchain-blackball' \! -name 'pasta-utils-deserialize' \! -name 'pasta-utils-object-sizes' \! -name '*.log' -exec mv -vn {} test-wallets \;
